import React from "react";
import "./ExpenseItem.css"
function ExpenseItem() {
  var date = new Date("2023-01-01");
  var lastMonth = date.getMonth() === 0?11 : date.getMonth();
  var Year = lastMonth === 0? date.getFullYear()-1 : date.getFullYear();
  var D_MY = new Date(Year, lastMonth+1,0);
  var mm = String(D_MY.getMonth());
  var dd = String(D_MY.getDate());
  var yyyy = String (D_MY.getFullYear());
  var N_dd=null
  var LastMonthDate= null
  var New_day = null
  var day =D_MY.toLocaleString('en-US', {weekday:"long"})
  if(day === "Sunday"){
     N_dd = dd-2
     LastMonthDate =new Date(yyyy,mm,N_dd)
     New_day =LastMonthDate.toLocaleString('en-US', {weekday:"long"})
    console.log(New_day)
    // console.log(date)
  }

 
  console.log(dd,day,yyyy,mm)
  // console.log(lastMonth)

  
  return (
    <div className="expense-item">
      <div>Dec 20 2020</div>
      <div className="expense-item__description">
        <h2>Bike inserence</h2> 
        <div className="expense-item__price">Amount</div>
      </div>
    </div>
  );
}

export default ExpenseItem;
